mapper.select:
	SELECT
		user_no, group_no, gm_no, paid, rec_date
		FROM gm_paid 
			
		WHERE user_no = #{user.userNo}
   		AND
   		group_no = #{group.groupNo}
   		AND
   		gm_no=#{groupMoney.gmNo}

mapper.update:
	   	UPDATE gm_paid
	   	
	   		paid = #{paid}, rec_date = to_char(sysdate, 'YYYY-MM-DD')
	   	
	   	WHERE 
	   		user_no = #{user.userNo}
	   		AND
	   		gm_no=#{groupMoney.gmNo}
